mapper.update:
UPDATE tb_board
SET hit = hit + 1
WHERE boardIdx = #{boardIdx}

mapper.insert:
INSERT INTO tb_board (categoryIdx, title, writer, content, password, hit, regdate)
VALUES (#{categoryIdx}, #{title}, #{writer}, #{content},
        #{password}, 0, NOW());

mapper.update:
UPDATE tb_board
SET title   = #{title},
    writer  = #{writer},
    content = #{content},
    moddate = #{modDate}
WHERE boardIdx = #{boardIdx}
  and password = #{password}

mapper.delete:
DELETE
FROM tb_board
WHERE boardIdx = #{boardIdx}
  and password = #{password}
